        public static string ChooseDB(string URL)
        {
            string InitialCatalog = "";
            string Platform = ConfigurationManager.AppSettings["platform"].ToString();
            if(Platform.Contains("Live"))
            {
                URL = "https://www.villaplus.com/";
            }
            else if(Platform.Contains("Test"))
            {
                URL = "https://staing1.villaplus.com/";
            }
            
            string DBURL =  URL + "code/info/versioninfo.asp";
            //string DBURL = "https://www.villaplus.com/code/info/versioninfo.asp";
            openBrowser_Map OpenBrowser = new openBrowser_Map();
            OpenBrowser.OpenBrowser_Method();
            BrowserWindow Win = new BrowserWindow();
            Win.NavigateToUrl(new System.Uri(DBURL));
            Win.SearchProperties[UITestControl.PropertyNames.ClassName] = "IEFrame";
            Win.SearchProperties[UITestControl.PropertyNames.Name] = ":::: Version Information ::::";
            HtmlDocument Doc = new HtmlDocument(Win);
            Doc.SearchProperties[UITestControl.PropertyNames.ControlType] = "Document";
            HtmlTable Table = new HtmlTable(Doc);
            Table.SearchProperties[UITestControl.PropertyNames.ControlType] = "Table";
            HtmlCell Cell = new HtmlCell(Table);
            Cell.SearchProperties[UITestControl.PropertyNames.ControlType] = "Cell";
            Cell.FilterProperties[UITestControl.PropertyNames.ClassName] = "HtmlTableCell";

            string TxtFromCell = Cell.InnerText.Replace("\r\n","").Replace("\t","").Replace(" ","").Trim();
            string DBConected = TxtFromCell.Substring(TxtFromCell.IndexOf(":"), 2).Replace(":","");

            if (DBConected == "A" && Platform.Contains("Live"))
            {
                InitialCatalog = "VP_WEB_LIVE_A";
            }
            else if (DBConected == "B" && Platform.Contains("Live"))
            {
                InitialCatalog = "VP_WEB_LIVE_B";
            }
            else if (DBConected == "A" && Platform.Contains("Test"))
            {
                InitialCatalog = "VP_WEB_Staging_A";
            }
            else if (DBConected == "B" && Platform.Contains("Test"))
            {
                InitialCatalog = "VP_WEB_Staging_B";
            }
            else
            {
                InitialCatalog = System.Configuration.ConfigurationManager.AppSettings["InitialCatalog"].ToString();
            }

            Win.Close();

            return InitialCatalog;
        }

